import React from 'react';
import logo from './logo.svg';
import './App.css';

var UserNameValue;

function SaveUserName(event) {
  UserNameValue=event.target.value;
}

function App() {
  return (
    <div className="App">
      <div>
        Welcome !<br></br>
        Enter your name
        <br>
        </br>
        <input name="UserNameInput" value={UserNameValue} onChange={(event)=>SaveUserName(event)}>
        </input>
        <button onClick={()=>console.log(UserNameValue)}>
          Save
        </button>
      </div>
    </div>
  );
}

export default App;
